
$(document).ready(function(){let prev_scrollY=0
let ticking=false
const contentHeight=$('#landing-content').offset().top
const nav=$('nav')
const navHeight=nav.height()
window.addEventListener('scroll',function(e){prev_scrollY=window.scrollY
if(!ticking&&prev_scrollY<contentHeight-navHeight){if(nav.hasClass('yellow'))
nav.removeClass('yellow')
window.requestAnimationFrame(function(){scaleFactor=(1+scrollY/30)
$('#logo-circle').css({"transform":"scale("+scaleFactor+")"})
ticking=false})
ticking=true}else{nav.addClass('yellow')}})
window.addEventListener('resize',function(e){navHeight=nav.height()
contentHeight=$('#landing-content').offset().top})
let people=['https://source.unsplash.com/200x201','https://source.unsplash.com/200x202','https://source.unsplash.com/200x203','https://source.unsplash.com/200x204','https://source.unsplash.com/200x205','https://source.unsplash.com/200x206','https://source.unsplash.com/200x207']
$profileNavImgs=$('#profile-nav img')
$profileImg=$('#profile-img img')
$('#profile-nav-up').click(()=>{people.push(people.shift())
$profileImg.attr('src',people[1])
$profileNavImgs.each(function(i){$(this).attr('src',people[i])})})
$('#profile-nav-down').click(()=>{people.unshift(people.pop())
$profileImg.attr('src',people[1])
$profileNavImgs.each(function(i){$(this).attr('src',people[i])})})
$profileNavImgs.click(function(){$(this).toggleClass('active')})})