
$(document).ready(function(){window.addEventListener('load',function(){const allimages=document.getElementsByTagName('img');for(let i=0;i<allimages.length;i++){if(allimages[i].getAttribute('data-src')){allimages[i].setAttribute('src',allimages[i].getAttribute('data-src'));}}},false)
let people=['https://source.unsplash.com/200x201','https://source.unsplash.com/200x202','https://source.unsplash.com/200x203','https://source.unsplash.com/200x204','https://source.unsplash.com/200x205','https://source.unsplash.com/200x206','https://source.unsplash.com/200x207']
$profileNavImgs=$('#profile-nav img')
$profileImg=$('#profile-img img')
$('#profile-nav-up').click(function(){handleScrollUp()})
$('#profile-nav-down').click(()=>{handleScrollDown()})
$('#profile-nav img').click(function(){$this=$(this)
$active=$('#profile-nav .active')
if($this.index()<$active.index())
handleScrollUp()
else if($this.index()>$active.index())
handleScrollDown()})
function handleScrollUp(){$('#profile-nav img').last().removeClass('hidden').insertBefore($('#profile-nav img').first())
updateHiddenAndActive()}
function handleScrollDown(){$('#profile-nav img').first().removeClass('hidden').insertAfter($('#profile-nav img').last())
updateHiddenAndActive()}
function updateHiddenAndActive(){$('#profile-nav .active').toggleClass('active')
$('#profile-nav>img:lt(3)').removeClass('hidden')
$('#profile-nav>img:gt(2)').addClass('hidden')
$('#profile-nav>img:nth-child(3)').addClass('active')
$profileImg.attr('src',$('#profile-nav .active').attr('src'))}})